import React, { useState } from 'react';
import NavBar from './Components/Navbar'
import Header from './Components/Header';
import { Container } from 'react-bootstrap';
import Catogery from './Components/catogery'
import Listitem from './Components/Listitem'
import data from './Components/dataApi'

const App = () => {

    const [Data, setData] = useState(data)
    const allcategores=["الكل", ...new Set(data.map((cat)=>cat.catogery))]
  console.log(allcategores)

    const filtercatogery = (cat) => {
        if (cat === 'الكل') {
            setData(data)
        } else {
            const filterfood = data.filter((cat) => cat.catogery === cat)
            setData(filterfood)
        }
    }

    return (
        <>
            <NavBar />
            <Container>
                <Header />
                <Catogery filtercatogery={filtercatogery} />
                <Listitem Data={Data} />
            </Container>
        </>

    )

}

export default App;
